import React, { useState, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { Search, ShoppingCart, TrendingDown, Package, Store, AlertCircle } from 'lucide-react';


export default function Dashboard() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedStore, setSelectedStore] = useState('All');
  
  useEffect(() => {
    // Fetch products from the backend
    const fetchProducts = async () => {
      try {
        setLoading(true);
        // Replace with your actual API endpoint
        const response = await fetch('/api/products/getAll');
        
        if (!response.ok) {
          throw new Error('Failed to fetch products');
        }
        
        const data = await response.json();
        setProducts(data);
      } catch (err) {
        console.error('Error fetching products:', err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    
    fetchProducts();
  }, []);
  
  // Group products by their ID to find the lowest price for each product
  const productsByLowestPrice = React.useMemo(() => {
    const productMap = {};
    
    products.forEach(product => {
      if (!productMap[product.productId]) {
        productMap[product.productId] = product;
      } else if (product.price < productMap[product.productId].price) {
        productMap[product.productId] = product;
      }
    });
    
    return Object.values(productMap);
  }, [products]);
  
  // Get unique stores
  const stores = React.useMemo(() => {
    const storeSet = new Set();
    products.forEach(product => {
      if (product.storeName) {
        storeSet.add(product.storeName);
      }
    });
    return ['All', ...Array.from(storeSet)];
  }, [products]);
  
  // Filter products based on search term and selected store
  const filteredProducts = React.useMemo(() => {
    return products.filter(product => {
      const matchesSearch = product.productDescription?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                           product.brandName?.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesStore = selectedStore === 'All' || product.storeName === selectedStore;
      return matchesSearch && matchesStore;
    });
  }, [products, searchTerm, selectedStore]);
  
  // Prepare data for the price comparison chart
  const chartData = React.useMemo(() => {
    const productPrices = {};
    
    filteredProducts.forEach(product => {
      if (!productPrices[product.productId]) {
        productPrices[product.productId] = {
          productId: product.productId,
          productName: product.productDescription?.length > 20 
            ? product.productDescription.substring(0, 20) + '...' 
            : product.productDescription,
        };
      }
      
      productPrices[product.productId][product.storeName] = product.price;
    });
    
    return Object.values(productPrices).slice(0, 6); // Limit to 6 products for readability
  }, [filteredProducts]);
  
  // Calculate price statistics
  const priceStats = React.useMemo(() => {
    if (filteredProducts.length === 0) return { avg: 0, min: 0, max: 0 };
    
    const prices = filteredProducts.map(p => p.price).filter(p => !isNaN(p) && p !== null);
    const sum = prices.reduce((a, b) => a + b, 0);
    
    return {
      avg: prices.length ? (sum / prices.length).toFixed(2) : 0,
      min: prices.length ? Math.min(...prices).toFixed(2) : 0,
      max: prices.length ? Math.max(...prices).toFixed(2) : 0
    };
  }, [filteredProducts]);
  
  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-lg">Loading products...</div>
      </div>
    );
  }
  
  if (error) {
    return (
      <div className="flex flex-col items-center justify-center h-screen">
        <AlertCircle size={48} className="text-red-500 mb-4" />
        <div className="text-lg text-red-500">Error: {error}</div>
      </div>
    );
  }
  
  return (
    <div className="p-6 max-w-full">
      <div className="mb-8">
        <h1 className="text-2xl font-bold mb-2">SmartPrices Dashboard</h1>
        <p className="text-gray-600">Find the best deals across multiple stores</p>
      </div>
      
      {/* Search and Filter Bar */}
      <div className="flex flex-wrap gap-4 mb-8">
        <div className="flex bg-white rounded shadow-sm items-center px-3 py-2 w-full md:w-auto flex-1">
          <Search className="mr-2 text-gray-400" size={20} />
          <input
            type="text"
            placeholder="Search products..."
            className="outline-none w-full"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        
        <select 
          className="rounded shadow-sm border border-gray-200 px-3 py-2"
          value={selectedStore}
          onChange={(e) => setSelectedStore(e.target.value)}
        >
          {stores.map(store => (
            <option key={store} value={store}>{store}</option>
          ))}
        </select>
      </div>
      
      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        <div className="bg-white p-6 rounded shadow-sm">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-gray-500 text-sm font-medium">Products</h3>
            <Package className="text-blue-500" size={20} />
          </div>
          <p className="text-2xl font-bold">{productsByLowestPrice.length}</p>
        </div>
        
        <div className="bg-white p-6 rounded shadow-sm">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-gray-500 text-sm font-medium">Average Price</h3>
            <TrendingDown className="text-green-500" size={20} />
          </div>
          <p className="text-2xl font-bold">R{priceStats.avg}</p>
        </div>
        
        <div className="bg-white p-6 rounded shadow-sm">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-gray-500 text-sm font-medium">Lowest Price</h3>
            <TrendingDown className="text-green-500" size={20} />
          </div>
          <p className="text-2xl font-bold">R{priceStats.min}</p>
        </div>
        
        <div className="bg-white p-6 rounded shadow-sm">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-gray-500 text-sm font-medium">Stores</h3>
            <Store className="text-purple-500" size={20} />
          </div>
          <p className="text-2xl font-bold">{stores.length - 1}</p>
        </div>
      </div>
      
      {/* Price Comparison Chart */}
      <div className="bg-white p-6 rounded shadow-sm mb-8">
        <h2 className="text-lg font-semibold mb-4">Price Comparison</h2>
        {chartData.length > 0 ? (
          <ResponsiveContainer width="100%" height={400}>
            <BarChart data={chartData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="productName" />
              <YAxis />
              <Tooltip />
              <Legend />
              {stores.filter(store => store !== 'All').map((store, index) => (
                <Bar 
                  key={store} 
                  dataKey={store} 
                  name={store} 
                  fill={`hsl(${index * 40}, 70%, 60%)`} 
                />
              ))}
            </BarChart>
          </ResponsiveContainer>
        ) : (
          <div className="flex justify-center items-center h-64 text-gray-400">
            No data available for the selected filters
          </div>
        )}
      </div>
      
      {/* Best Deals Table */}
      <div className="bg-white p-6 rounded shadow-sm">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-lg font-semibold">Best Deals</h2>
          <ShoppingCart size={20} className="text-gray-400" />
        </div>
        
        {productsByLowestPrice.length > 0 ? (
          <div className="overflow-x-auto">
            <table className="min-w-full table-auto">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Product</th>
                  <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Brand</th>
                  <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Store</th>
                  <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Price</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200">
                {productsByLowestPrice.slice(0, 5).map((product, index) => (
                  <tr key={`${product.productId}-${index}`}>
                    <td className="px-4 py-3">{product.productDescription}</td>
                    <td className="px-4 py-3">{product.brandName}</td>
                    <td className="px-4 py-3">{product.storeName}</td>
                    <td className="px-4 py-3 font-medium">R{product.price?.toFixed(2)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <div className="flex justify-center items-center h-32 text-gray-400">
            No products available
          </div>
        )}
      </div>
    </div>
  );
}
