import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);
  const [cart, setCart] = useState([]);
  const [cartLoading, setCartLoading] = useState(false);

  // Initialize auth state from localStorage
  useEffect(() => {
    const initializeAuth = async () => {
      const token = localStorage.getItem('token');
      const userData = localStorage.getItem('user');
      
      if (token && userData) {
        try {
          const response = await axios.get('http://localhost:8081/auth/verify', {
            headers: { Authorization: `Bearer ${token}` }
          });
          
          if (response.data.valid) {
            setIsAuthenticated(true);
            const parsedUser = JSON.parse(userData);
            setUser(parsedUser);
            
            // Load cart for authenticated user
            await loadCartItems(token, parsedUser.email);
          } else {
            clearAuth();
          }
        } catch (error) {
          clearAuth();
        }
      }
      setLoading(false);
    };
    
    initializeAuth();
  }, []);

  // Load cart items from backend
  const loadCartItems = async (token, email) => {
    setCartLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:8081/api/customers/${email}/cart`,
        { 
          headers: { Authorization: `Bearer ${token}` },
          transformResponse: [], // Prevent auto-parsing
          responseType: 'json'
        }
      );

      // Handle potential string response
      const responseData = typeof response.data === 'string' 
        ? JSON.parse(response.data)
        : response.data;

      const cartItems = responseData?.items || [];
      setCart(cartItems);
    } catch (error) {
      console.error('Cart load error:', {
        error: error.response?.data || error.message,
        status: error.response?.status
      });
      setCart([]);
    } finally {
      setCartLoading(false);
    }
  };

  // Login function with cart loading
  const login = async (token, userData) => {
    if (!userData || typeof userData !== 'object' || !userData.email) {
      throw new Error('Invalid user data from server');
    }
  
    localStorage.setItem('token', token);
    localStorage.setItem('user', JSON.stringify(userData));
    setIsAuthenticated(true);
    setUser(userData);
    
    // Load cart after successful login
    await loadCartItems(token, userData.email);
  };

  // Add item to cart
  const addToCart = async (item) => {
    const token = localStorage.getItem('token');
    const userData = user || JSON.parse(localStorage.getItem('user'));
    
    if (!token || !userData?.email) {
      throw new Error('Authentication required');
    }

    setCartLoading(true);
    try {
      const response = await axios.post(
        `http://localhost:8081/api/customers/${userData.email}/cart`,
        item,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      // Refresh cart after successful addition
      await loadCartItems(token, userData.email);
      return true;
    } catch (error) {
      console.error('Add to cart error:', error.response?.data || error.message);
      return false;
    } finally {
      setCartLoading(false);
    }
  };

  // Remove item from cart
  const removeFromCart = async (index) => {
    const token = localStorage.getItem('token');
    const userData = user || JSON.parse(localStorage.getItem('user'));
    
    if (!token || !userData?.email) {
      throw new Error('Authentication required');
    }

    setCartLoading(true);
    try {
      await axios.delete(
        `http://localhost:8081/api/customers/${userData.email}/cart/${index}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      // Refresh cart after successful removal
      await loadCartItems(token, userData.email);
      return true;
    } catch (error) {
      console.error('Remove from cart error:', error.response?.data || error.message);
      return false;
    } finally {
      setCartLoading(false);
    }
  };

  // Clear entire cart
  const clearCart = async () => {
    const token = localStorage.getItem('token');
    const userData = user || JSON.parse(localStorage.getItem('user'));
    
    if (!token || !userData?.email) {
      throw new Error('Authentication required');
    }

    setCartLoading(true);
    try {
      await axios.delete(
        `http://localhost:8081/api/customers/${userData.email}/cart`,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setCart([]);
      return true;
    } catch (error) {
      console.error('Clear cart error:', error.response?.data || error.message);
      return false;
    } finally {
      setCartLoading(false);
    }
  };

  // Sync entire cart (for batch updates)
  const syncCart = async (cartItems) => {
    const token = localStorage.getItem('token');
    const userData = user || JSON.parse(localStorage.getItem('user'));
    
    if (!token || !userData?.email) {
      throw new Error('Authentication required');
    }

    setCartLoading(true);
    try {
      const response = await axios.post(
        `http://localhost:8081/api/customers/${userData.email}/cart/sync`,
        { items: cartItems },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const updatedItems = response.data?.items || [];
      setCart(updatedItems);
      return true;
    } catch (error) {
      console.error('Sync cart error:', error.response?.data || error.message);
      return false;
    } finally {
      setCartLoading(false);
    }
  };

  // Logout function
  const logout = () => {
    clearAuth();
  };

  // Clear authentication data
  const clearAuth = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setIsAuthenticated(false);
    setUser(null);
    setCart([]);
  };

  return (
    <AuthContext.Provider value={{
      isAuthenticated,
      user,
      loading,
      cart,
      cartLoading,
      loadCartItems,
      addToCart,
      removeFromCart,
      clearCart,
      syncCart,
      login,
      logout
    }}>
      {children}
    </AuthContext.Provider>
  );
}

export const useAuth = () => useContext(AuthContext);
