import React, { useState, useEffect } from 'react';
import { toast } from 'react-toastify';
import './HotDeals.css';

const HotDeals = ({ handleAddToCart }) => {
  const [chatMessage, setChatMessage] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const sendChat = async () => {
    if (!chatMessage.trim() || isLoading) return;

    try {
      setIsLoading(true);
      // Add user message
      setChatHistory(prev => [...prev, { 
        type: 'user', 
        content: chatMessage,
        timestamp: new Date().toISOString()
      }]);

      const res = await fetch('http://localhost:8081/api/chatbot/ask', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({ message: chatMessage }),
      });

      const data = await res.json();
      
      if (!res.ok) throw new Error(data.error || 'Chat request failed');

      setChatHistory(prev => [...prev, {
        type: 'bot',
        content: data.length > 0 
          ? `Found ${data.length} matching ${data.length === 1 ? 'product' : 'products'}:`
          : "No products found matching your query.",
        products: data,
        timestamp: new Date().toISOString()
      }]);

      setChatMessage('');
    } catch (err) {
      toast.error(err.message || 'Chatbot service unavailable');
      console.error('Chat error:', err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="hot-deals-container">
      <div className="chat-container">
        <div className="chat-messages">
          {chatHistory.map((msg, index) => (
            <div key={`msg-${index}-${msg.timestamp}`} className={`message ${msg.type}`}>
              <div className="message-content">
                <div className="message-bubble">
                  <p>{msg.content}</p>
                  {msg.products && (
                    <div className="products-grid">
                      {msg.products.map(product => (
                        <div key={product._id} className="product-card">
                          <img 
                            src={product.productImageUrl} 
                            alt={product.productDescription}
                            className="product-image"
                          />
                          <div className="product-details">
                            <h4 className="product-brand">{product.brandName}</h4>
                            <p className="product-description">{product.productDescription}</p>
                            <p className="product-price">{product.price}</p>
                            <button 
                              className="add-to-cart-btn"
                              onClick={() => handleAddToCart(product)}
                            >
                              Add to Cart
                            </button>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
                <span className="message-time">
                  {new Date(msg.timestamp).toLocaleTimeString([], { 
                    hour: '2-digit', 
                    minute: '2-digit' 
                  })}
                </span>
              </div>
            </div>
          ))}
        </div>

        <div className="chat-input-container">
          <input
            type="text"
            value={chatMessage}
            onChange={(e) => setChatMessage(e.target.value)}
            placeholder="Ask about products (e.g. 'Show me Apple phones under R20000')"
            onKeyPress={(e) => e.key === 'Enter' && sendChat()}
            disabled={isLoading}
            className="chat-input"
          />
          <button 
            onClick={sendChat}
            disabled={isLoading}
            className="send-button"
          >
            {isLoading ? 'Sending...' : 'Send'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default HotDeals;
