import React, { useState, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { 
  Search, 
  ShoppingCart, 
  TrendingDown, 
  Package, 
  Store, 
  AlertCircle,
  LayoutDashboard,
  LineChart,
  List,
  Settings,
  LogOut,
  Menu,
  X
} from 'lucide-react';
import './Dashboard.css';

export default function Dashboard() {
  const [products, setProducts] = useState([]);
  const [customer, setCustomer] = useState(null);
  const [loading, setLoading] = useState(true);
  const [customerLoading, setCustomerLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedStore, setSelectedStore] = useState('All');
  const [isScraping, setIsScraping] = useState(false);
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [activeTab, setActiveTab] = useState('dashboard');
  
  const fetchCustomerData = async () => {
    try {
      setCustomerLoading(true);
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('No authentication token found');
      }

      const decodedToken = jwtDecode(token);
      const email = decodedToken.email;

      const response = await fetch(`http://localhost:8080/api/customers/${email}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (!response.ok) {
        throw new Error('Failed to fetch customer data');
      }

      const customerData = await response.json();
      setCustomer(customerData);
    } catch (error) {
      console.error('Error fetching customer:', error);
    } finally {
      setCustomerLoading(false);
    }
  };

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const response = await fetch('http://localhost:8080/api/dbproducts/getAll');
      
      if (!response.ok) {
        throw new Error(`Failed to fetch products: ${response.status}`);
      }
      
      const contentType = response.headers.get('content-type');
      if (!contentType || !contentType.includes('application/json')) {
        throw new Error('Received non-JSON response');
      }
      
      const data = await response.json();
      
      if (!Array.isArray(data)) {
        throw new Error('Expected array of products but received different structure');
      }
      
      const validatedProducts = data.map(product => ({
        ...product,
        id: product._id || product.id,
        price: product.price || '0',
        productDescription: product.productDescription || 'No description',
        storeName: product.storeName || 'Unknown store'
      }));
      
      setProducts(validatedProducts);
      setError(null);
    } catch (err) {
      console.error('Error fetching products:', err);
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts();
    fetchCustomerData();
  }, []);

  const handleSearch = async () => {
    if (!searchTerm.trim()) return;
    
    try {
      setIsScraping(true);
      setLoading(true);
      setError(null);
      
      const response = await fetch(`http://localhost:8080/api/dbproducts/search?term=${encodeURIComponent(searchTerm)}`);
      
      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.message || `Search failed with status ${response.status}`);
      }
      
      const data = await response.json();
      
      if (data.length === 0) {
        setError("No products found. The system is scraping data - please check back in a few minutes.");
        setIsScraping(false);
        return;
      }
      
      setProducts(data);
    } catch (err) {
      console.error('Search error:', err);
      setError(err.message || "No products found for the search term. Please wait a few minutes as AI is fetching the data.");
    } finally {
      setLoading(false);
    }
  };
  
  const productsByLowestPrice = React.useMemo(() => {
    const productMap = {};
    
    products.forEach(product => {
      if (!productMap[product._id]) {
        productMap[product._id] = product;
      } else if (parseFloat(product.price) < parseFloat(productMap[product._id].price)) {
        productMap[product._id] = product;
      }
    });
    
    return Object.values(productMap);
  }, [products]);
  
  const stores = React.useMemo(() => {
    const storeSet = new Set();
    products.forEach(product => {
      if (product.storeName) {
        storeSet.add(product.storeName);
      }
    });
    return ['All', ...Array.from(storeSet)];
  }, [products]);
  
  const filteredProducts = React.useMemo(() => {
    return products.filter(product => {
      const matchesSearch = product.productDescription?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                           product.brandName?.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesStore = selectedStore === 'All' || product.storeName === selectedStore;
      return matchesSearch && matchesStore;
    });
  }, [products, searchTerm, selectedStore]);
  
  const chartData = React.useMemo(() => {
    const productPrices = {};
    
    filteredProducts.forEach(product => {
      if (!product.id) return;
      
      if (!productPrices[product.id]) {
        productPrices[product.id] = {
          productId: product.id,
          productName: product.productDescription?.length > 20 
            ? product.productDescription.substring(0, 20) + '...' 
            : product.productDescription || 'Unnamed Product',
        };
      }
      
      const price = parseFloat(product.price);
      if (!isNaN(price) && product.storeName) {
        productPrices[product.id][product.storeName] = price;
      }
    });
    
    return Object.values(productPrices).slice(0, 6);
  }, [filteredProducts]);
  
  const priceStats = React.useMemo(() => {
    if (filteredProducts.length === 0) return { avg: 0, min: 0, max: 0 };
    
    const prices = filteredProducts.map(p => parseFloat(p.price)).filter(p => !isNaN(p));
    const sum = prices.reduce((a, b) => a + b, 0);
    
    return {
      avg: prices.length ? (sum / prices.length).toFixed(2) : 0,
      min: prices.length ? Math.min(...prices).toFixed(2) : 0,
      max: prices.length ? Math.max(...prices).toFixed(2) : 0
    };
  }, [filteredProducts]);

  if (loading) {
    return (
      <div className="loading-screen">
        <div className="loading-spinner"></div>
        <div className="loading-text">
          {isScraping ? 'Scraping for products...' : 'Loading products...'}
        </div>
      </div>
    );
  }
  
  if (error) {
    return (
      <div className="error-screen">
        <AlertCircle size={48} className="error-icon" />
        <div className="error-message">{error}</div>
        <button 
          onClick={() => {
            setError(null);
            setSearchTerm('');
            setSelectedStore('All');
          }}
          className="retry-button"
        >
          Try Again
        </button>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      {/* Sidebar */}
      <div className={`sidebar ${sidebarOpen ? 'open' : 'closed'}`}>
        <div className="sidebar-header">
          {sidebarOpen ? (
            <div className="brand-container">
              <img src="/images/smileShop.png" alt="SmartPrices Logo" className="brand-logo" />
              <span className="brand-name">SmartPrices</span>
              <button 
                onClick={() => setSidebarOpen(false)}
                className="close-sidebar"
              >
                <X size={20} />
              </button>
            </div>
          ) : (
            <button 
              onClick={() => setSidebarOpen(true)}
              className="open-sidebar-btn"
            >
              <Menu size={24} />
            </button>
          )}
        </div>

        {sidebarOpen && (
          <>
            <div className="sidebar-menu">
              <button
                onClick={() => setActiveTab('dashboard')}
                className={`menu-item ${activeTab === 'dashboard' ? 'active' : ''}`}
              >
                <LayoutDashboard className="menu-icon" />
                <span className="menu-text">Dashboard</span>
              </button>
              
              <button
                onClick={() => setActiveTab('analytics')}
                className={`menu-item ${activeTab === 'analytics' ? 'active' : ''}`}
              >
                <LineChart className="menu-icon" />
                <span className="menu-text">Analytics</span>
              </button>
              
              <button
                onClick={() => setActiveTab('products')}
                className={`menu-item ${activeTab === 'products' ? 'active' : ''}`}
              >
                <List className="menu-icon" />
                <span className="menu-text">Products</span>
              </button>
              
              <button
                onClick={() => setActiveTab('settings')}
                className={`menu-item ${activeTab === 'settings' ? 'active' : ''}`}
              >
                <Settings className="menu-icon" />
                <span className="menu-text">Settings</span>
              </button>
            </div>

            <div className="sidebar-footer">
              <div className="user-profile">
                <div className="user-avatar">
                  {customer ? customer.name?.charAt(0).toUpperCase() : 'U'}
                </div>
                {!customerLoading && customer && (
                  <div className="user-info">
                    <div className="user-name">{customer.name}</div>
                    <div className="user-email">{customer.email}</div>
                  </div>
                )}
                <button className="logout-btn">
                  <LogOut size={20} />
                </button>
              </div>
            </div>
          </>
        )}
      </div>

      {/* Main Content */}
      <div className="main-content">
        {!sidebarOpen && (
          <button 
            onClick={() => setSidebarOpen(true)}
            className="floating-menu-btn"
          >
            <Menu size={24} />
          </button>
        )}

        <div className="content-container">
          {/* Header */}
          <div className="dashboard-header">
            <h1>SmartPrices Dashboard</h1>
            <p>Find the best deals across multiple stores</p>
          </div>
          
          {/* Search and Filter */}
          <div className="search-filter-container">
            <div className="search-bar">
              <Search className="search-icon" />
              <input
                type="text"
                placeholder="Search products..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
              />
              <button 
                onClick={handleSearch}
                disabled={isScraping}
                className="search-button"
              >
                {isScraping ? (
                  <span className="flex items-center">
                    <span className="spinner"></span>
                    <span className="ml-2">Searching...</span>
                  </span>
                ) : 'Search'}
              </button>
            </div>
            
            <select 
              value={selectedStore}
              onChange={(e) => setSelectedStore(e.target.value)}
              className="store-selector"
            >
              {stores.map(store => (
                <option key={store} value={store}>{store}</option>
              ))}
            </select>
          </div>
          
          {/* Stats Cards */}
          <div className="stats-grid">
            <div className="stat-card">
              <div className="stat-header">
                <h3>Products</h3>
                <Package className="stat-icon blue" />
              </div>
              <div className="stat-value">{productsByLowestPrice.length}</div>
            </div>
            
            <div className="stat-card">
              <div className="stat-header">
                <h3>Average Price</h3>
                <TrendingDown className="stat-icon green" />
              </div>
              <div className="stat-value">R{priceStats.avg}</div>
            </div>
            
            <div className="stat-card">
              <div className="stat-header">
                <h3>Lowest Price</h3>
                <TrendingDown className="stat-icon green" />
              </div>
              <div className="stat-value">R{priceStats.min}</div>
            </div>
            
            <div className="stat-card">
              <div className="stat-header">
                <h3>Stores</h3>
                <Store className="stat-icon purple" />
              </div>
              <div className="stat-value">{stores.length - 1}</div>
            </div>
          </div>
          
          {/* Price Comparison Chart */}
          <div className="chart-container">
            <h2>Price Comparison</h2>
            {chartData.length > 0 ? (
              <ResponsiveContainer width="100%" height={400}>
                <BarChart data={chartData}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
                  <XAxis dataKey="productName" stroke="#888" />
                  <YAxis stroke="#888" />
                  <Tooltip 
                    contentStyle={{
                      backgroundColor: '#2d3748',
                      borderColor: '#4a5568',
                      borderRadius: '0.5rem',
                      color: 'white'
                    }}
                  />
                  <Legend />
                  {stores.filter(store => store !== 'All').map((store, index) => (
                    <Bar 
                      key={store} 
                      dataKey={store} 
                      name={store} 
                      fill={`hsl(${index * 40 + 200}, 70%, 50%)`}
                      radius={[4, 4, 0, 0]}
                    />
                  ))}
                </BarChart>
              </ResponsiveContainer>
            ) : (
              <div className="empty-state">
                {searchTerm ? 'No products found. Try a different search term.' : 'No products available'}
              </div>
            )}
          </div>
          
          {/* Best Deals Table */}
          <div className="deals-container">
            <div className="deals-header">
              <h2>Best Deals</h2>
              <ShoppingCart className="cart-icon" />
            </div>
            
            {productsByLowestPrice.length > 0 ? (
              <div className="table-container">
                <table>
                  <thead>
                    <tr>
                      <th>Product</th>
                      <th>Brand</th>
                      <th>Store</th>
                      <th>Price</th>
                    </tr>
                  </thead>
                  <tbody>
                    {productsByLowestPrice.slice(0, 5).map((product) => (
                      <tr key={product._id}>
                        <td>{product.productDescription}</td>
                        <td>{product.brandName}</td>
                        <td>{product.storeName}</td>
                        <td className="price-cell">R{parseFloat(product.price).toFixed(2)}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ) : (
              <div className="empty-state">
                No products available
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
