import React, { useState, useEffect } from 'react';
import './Cart.css';
import { useAuth } from './AuthContext';
import { useNavigate } from 'react-router-dom';

const Cart = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [cartItems, setCartItems] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!user) {
      navigate('/login');
      return;
    }
    loadCart();
  }, [user, navigate]);

  const loadCart = async () => {
    try {
      setIsLoading(true);
      const response = await fetch(`/api/customers/${user.email}/cart`);
      
      if (!response.ok) throw new Error('Failed to load cart');
      
      const data = await response.json();
      setCartItems(data.items || []);
      setError(null);
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  const removeItem = async (index) => {
    try {
      const response = await fetch(`/api/customers/${user.email}/cart/${index}`, {
        method: 'DELETE'
      });
      
      if (!response.ok) throw new Error('Failed to remove item');
      
      // Optimistic update
      setCartItems(prev => prev.filter((_, i) => i !== index));
    } catch (err) {
      setError(err.message);
      loadCart(); // Refresh cart if error occurs
    }
  };

  if (isLoading) return <div className="cart-container"><h2>Loading cart...</h2></div>;
  if (error) return <div className="cart-container"><h2>Error: {error}</h2></div>;

  return (
    <div className="cart-container">
      <h2>Your Cart</h2>
      
      {cartItems.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <>
          <div className="cart-items">
            {cartItems.map((item, index) => (
              <div className="cart-item" key={`${item._id}-${index}`}>
                {/* ... (keep your item display) */}
                <button onClick={() => removeItem(index)}>Remove</button>
              </div>
            ))}
          </div>
          
          <div className="cart-summary">
            <h3>Total: R{cartItems.reduce((sum, item) => sum + (item.price || 0), 0).toFixed(2)}</h3>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
