import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';

const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);
  const [cart, setCart] = useState([]);
  const [cartLoading, setCartLoading] = useState(false);

  useEffect(() => {
    const initializeAuth = async () => {
      const token = localStorage.getItem('token');
      const userData = localStorage.getItem('user');
      
      if (token && userData) {
        try {
          const response = await axios.get('http://localhost:8081/auth/verify', {
            headers: { Authorization: `Bearer ${token}` }
          });
          
          if (response.data.valid) {
            const parsedUser = JSON.parse(userData);
            setIsAuthenticated(true);
            setUser(parsedUser);
            
            // Load cart only if user data contains email
            if (parsedUser.email) {
              await loadCartItems(token, parsedUser.email);
            }
          } else {
            clearAuth();
          }
        } catch (error) {
          clearAuth();
        }
      }
      setLoading(false);
    };
    
    initializeAuth();
  }, []);

  const loadCartItems = async (token, email) => {
    setCartLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:8081/api/customers/${email}/cart`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const cartItems = response.data.items || [];
      setCart(cartItems);
      localStorage.setItem('cart', JSON.stringify(cartItems));
    } catch (error) {
      console.error('Failed to load cart:', error);
      toast.error('Failed to load cart items');
    } finally {
      setCartLoading(false);
    }
  };

  const login = async (token, userData) => {
    try {
      // Ensure userData has required properties
      if (!userData || !userData.email) {
        throw new Error('Invalid user data received');
      }

      localStorage.setItem('token', token);
      localStorage.setItem('user', JSON.stringify(userData));
      setIsAuthenticated(true);
      setUser(userData);
      
      // Load cart after successful login
      await loadCartItems(token, userData.email);
      
      return true;
    } catch (error) {
      console.error('Login error:', error);
      clearAuth();
      return false;
    }
  };

  const logout = () => {
    clearAuth();
    toast.success('Logged out successfully');
  };

  const clearAuth = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    localStorage.removeItem('cart');
    setIsAuthenticated(false);
    setUser(null);
    setCart([]);
  };

  const updateCart = async (cartItems) => {
    if (!user || !user.email) return;
    
    try {
      setCartLoading(true);
      await axios.post(
        `http://localhost:8081/api/customers/${user.email}/cart/sync`,
        { items: cartItems },
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );
      setCart(cartItems);
      localStorage.setItem('cart', JSON.stringify(cartItems));
    } catch (error) {
      console.error('Failed to update cart:', error);
      toast.error('Failed to update cart');
    } finally {
      setCartLoading(false);
    }
  };

  return (
    <AuthContext.Provider value={{ 
      isAuthenticated, 
      user,
      loading,
      cart,
      cartLoading,
      login,
      logout,
      updateCart
    }}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}
