import React, { useState, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { 
  Search, 
  ShoppingCart, 
  TrendingDown, 
  Package, 
  Store, 
  AlertCircle,
  LayoutDashboard,
  LineChart,
  List,
  Settings,
  LogOut,
  Menu
} from 'lucide-react';
import './Dashboard.css';
export default function Dashboard() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedStore, setSelectedStore] = useState('All');
  const [isScraping, setIsScraping] = useState(false);
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [activeTab, setActiveTab] = useState('dashboard');
  
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const response = await fetch('/api/dbproducts/getAll');
        
        if (!response.ok) {
          throw new Error('Failed to fetch products');
        }
        
        const data = await response.json();
        setProducts(data);
      } catch (err) {
        console.error('Error fetching products:', err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    
    fetchProducts();
  }, []);

  const handleSearch = async () => {
    if (!searchTerm.trim()) return;
    
    try {
      setIsScraping(true);
      setLoading(true);
      const response = await fetch(`/api/dbproducts/search?term=${encodeURIComponent(searchTerm)}`);
      
      if (!response.ok) {
        throw new Error('Search failed');
      }
      
      const data = await response.json();
      setProducts(data);
    } catch (err) {
      console.error('Search error:', err);
      setError(err.message);
    } finally {
      setIsScraping(false);
      setLoading(false);
    }
  };
  
  const productsByLowestPrice = React.useMemo(() => {
    const productMap = {};
    
    products.forEach(product => {
      if (!productMap[product._id]) {
        productMap[product._id] = product;
      } else if (parseFloat(product.price) < parseFloat(productMap[product._id].price)) {
        productMap[product._id] = product;
      }
    });
    
    return Object.values(productMap);
  }, [products]);
  
  const stores = React.useMemo(() => {
    const storeSet = new Set();
    products.forEach(product => {
      if (product.storeName) {
        storeSet.add(product.storeName);
      }
    });
    return ['All', ...Array.from(storeSet)];
  }, [products]);
  
  const filteredProducts = React.useMemo(() => {
    return products.filter(product => {
      const matchesSearch = product.productDescription?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                           product.brandName?.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesStore = selectedStore === 'All' || product.storeName === selectedStore;
      return matchesSearch && matchesStore;
    });
  }, [products, searchTerm, selectedStore]);
  
  const chartData = React.useMemo(() => {
    const productPrices = {};
    
    filteredProducts.forEach(product => {
      if (!productPrices[product._id]) {
        productPrices[product._id] = {
          productId: product._id,
          productName: product.productDescription?.length > 20 
            ? product.productDescription.substring(0, 20) + '...' 
            : product.productDescription,
        };
      }
      
      productPrices[product._id][product.storeName] = parseFloat(product.price);
    });
    
    return Object.values(productPrices).slice(0, 6);
  }, [filteredProducts]);
  
  const priceStats = React.useMemo(() => {
    if (filteredProducts.length === 0) return { avg: 0, min: 0, max: 0 };
    
    const prices = filteredProducts.map(p => parseFloat(p.price)).filter(p => !isNaN(p));
    const sum = prices.reduce((a, b) => a + b, 0);
    
    return {
      avg: prices.length ? (sum / prices.length).toFixed(2) : 0,
      min: prices.length ? Math.min(...prices).toFixed(2) : 0,
      max: prices.length ? Math.max(...prices).toFixed(2) : 0
    };
  }, [filteredProducts]);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-lg">
          {isScraping ? 'Scraping for products...' : 'Loading products...'}
        </div>
      </div>
    );
  }
  
  if (error) {
    return (
      <div className="flex flex-col items-center justify-center h-screen">
        <AlertCircle size={48} className="text-red-500 mb-4" />
        <div className="text-lg text-red-500">Error: {error}</div>
        <button 
          onClick={() => setError(null)}
          className="mt-4 px-4 py-2 bg-blue-500 text-white rounded"
        >
          Try Again
        </button>
      </div>
    );
  }
  
  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <div className={`sidebar bg-gray-900 text-white ${sidebarOpen ? 'w-64' : 'w-20'} transition-all duration-300 ease-in-out`}>
        <div className="sidebar-header p-4 border-b border-gray-700 flex items-center justify-between">
          {sidebarOpen ? (
            <div className="flex items-center">
              <div className="brand-logo-sidebar w-8 h-8 bg-gradient-to-r from-purple-600 to-blue-500 rounded-md mr-2"></div>
              <span className="sidebar-title text-xl font-bold bg-gradient-to-r from-purple-400 to-blue-400 bg-clip-text text-transparent">
                SmartPrices
              </span>
            </div>
          ) : (
            <div className="brand-logo-sidebar w-8 h-8 bg-gradient-to-r from-purple-600 to-blue-500 rounded-md mx-auto"></div>
          )}
          <button 
            onClick={() => setSidebarOpen(!sidebarOpen)}
            className="text-gray-400 hover:text-white"
          >
            <Menu size={20} />
          </button>
        </div>
        
        <div className="sidebar-menu p-4">
          <button
            onClick={() => setActiveTab('dashboard')}
            className={`menu-item w-full flex items-center p-3 rounded-lg mb-2 ${activeTab === 'dashboard' ? 'bg-gray-800 text-white' : 'text-gray-400 hover:bg-gray-800 hover:text-white'}`}
          >
            <LayoutDashboard className="menu-icon mr-3" size={20} />
            {sidebarOpen && <span className="menu-text">Dashboard</span>}
          </button>
          
          <button
            onClick={() => setActiveTab('analytics')}
            className={`menu-item w-full flex items-center p-3 rounded-lg mb-2 ${activeTab === 'analytics' ? 'bg-gray-800 text-white' : 'text-gray-400 hover:bg-gray-800 hover:text-white'}`}
          >
            <LineChart className="menu-icon mr-3" size={20} />
            {sidebarOpen && <span className="menu-text">Analytics</span>}
          </button>
          
          <button
            onClick={() => setActiveTab('products')}
            className={`menu-item w-full flex items-center p-3 rounded-lg mb-2 ${activeTab === 'products' ? 'bg-gray-800 text-white' : 'text-gray-400 hover:bg-gray-800 hover:text-white'}`}
          >
            <List className="menu-icon mr-3" size={20} />
            {sidebarOpen && <span className="menu-text">Products</span>}
          </button>
          
          <button
            onClick={() => setActiveTab('settings')}
            className={`menu-item w-full flex items-center p-3 rounded-lg ${activeTab === 'settings' ? 'bg-gray-800 text-white' : 'text-gray-400 hover:bg-gray-800 hover:text-white'}`}
          >
            <Settings className="menu-icon mr-3" size={20} />
            {sidebarOpen && <span className="menu-text">Settings</span>}
          </button>
        </div>
        
        <div className="sidebar-footer absolute bottom-0 w-full p-4 border-t border-gray-700">
          <div className="user-profile flex items-center">
            <div className="user-avatar w-10 h-10 rounded-full bg-gradient-to-r from-purple-500 to-blue-500 flex items-center justify-center text-white font-bold">
              AU
            </div>
            {sidebarOpen && (
              <div className="user-info ml-3">
                <div className="user-name text-sm font-medium">Admin User</div>
                <div className="user-email text-xs text-gray-400">admin@smartprices.com</div>
              </div>
            )}
            <button className="logout-btn ml-auto text-gray-400 hover:text-white">
              <LogOut size={20} />
            </button>
          </div>
        </div>
      </div>
      
      {/* Main Content */}
      <div className="flex-1 overflow-auto">
        <div className="p-6 max-w-full">
          {/* Header */}
          <div className="mb-8">
            <h1 className="text-2xl font-bold mb-2">SmartPrices Dashboard</h1>
            <p className="text-gray-600">Find the best deals across multiple stores</p>
          </div>
          
          {/* Search and Filter */}
          <div className="flex flex-wrap gap-4 mb-8">
            <div className="flex bg-white rounded-lg shadow-sm items-center px-3 py-2 w-full md:w-auto flex-1">
              <Search className="mr-2 text-gray-400" size={20} />
              <input
                type="text"
                placeholder="Search products..."
                className="outline-none w-full bg-transparent"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
              />
              <button 
                onClick={handleSearch}
                className="ml-2 px-4 py-1 bg-gradient-to-r from-purple-600 to-blue-500 text-white rounded-lg transition-all hover:opacity-90"
                disabled={isScraping}
              >
                {isScraping ? 'Searching...' : 'Search'}
              </button>
            </div>
            
            <select 
              className="rounded-lg shadow-sm border border-gray-200 px-3 py-2 bg-white"
              value={selectedStore}
              onChange={(e) => setSelectedStore(e.target.value)}
            >
              {stores.map(store => (
                <option key={store} value={store}>{store}</option>
              ))}
            </select>
          </div>
          
          {/* Stats Cards */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-100">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-gray-500 text-sm font-medium">Products</h3>
                <Package className="text-blue-500" size={20} />
              </div>
              <p className="text-2xl font-bold">{productsByLowestPrice.length}</p>
            </div>
            
            <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-100">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-gray-500 text-sm font-medium">Average Price</h3>
                <TrendingDown className="text-green-500" size={20} />
              </div>
              <p className="text-2xl font-bold">R{priceStats.avg}</p>
            </div>
            
            <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-100">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-gray-500 text-sm font-medium">Lowest Price</h3>
                <TrendingDown className="text-green-500" size={20} />
              </div>
              <p className="text-2xl font-bold">R{priceStats.min}</p>
            </div>
            
            <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-100">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-gray-500 text-sm font-medium">Stores</h3>
                <Store className="text-purple-500" size={20} />
              </div>
              <p className="text-2xl font-bold">{stores.length - 1}</p>
            </div>
          </div>
          
          {/* Price Comparison Chart */}
          <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-100 mb-8">
            <h2 className="text-lg font-semibold mb-4">Price Comparison</h2>
            {chartData.length > 0 ? (
              <ResponsiveContainer width="100%" height={400}>
                <BarChart data={chartData}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#f0f0f0" />
                  <XAxis dataKey="productName" stroke="#888" />
                  <YAxis stroke="#888" />
                  <Tooltip 
                    contentStyle={{
                      backgroundColor: '#2d3748',
                      borderColor: '#4a5568',
                      borderRadius: '0.5rem',
                      color: 'white'
                    }}
                  />
                  <Legend />
                  {stores.filter(store => store !== 'All').map((store, index) => (
                    <Bar 
                      key={store} 
                      dataKey={store} 
                      name={store} 
                      fill={`hsl(${index * 40 + 200}, 70%, 50%)`}
                      radius={[4, 4, 0, 0]}
                    />
                  ))}
                </BarChart>
              </ResponsiveContainer>
            ) : (
              <div className="flex justify-center items-center h-64 text-gray-400">
                {searchTerm ? 'No products found. Try a different search term.' : 'No products available'}
              </div>
            )}
          </div>
          
          {/* Best Deals Table */}
          <div className="bg-white p-6 rounded-lg shadow-sm border border-gray-100">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-lg font-semibold">Best Deals</h2>
              <ShoppingCart size={20} className="text-gray-400" />
            </div>
            
            {productsByLowestPrice.length > 0 ? (
              <div className="overflow-x-auto">
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Product</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Brand</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Store</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Price</th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {productsByLowestPrice.slice(0, 5).map((product) => (
                      <tr key={product._id} className="hover:bg-gray-50">
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{product.productDescription}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{product.brandName}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{product.storeName}</td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">R{parseFloat(product.price).toFixed(2)}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            ) : (
              <div className="flex justify-center items-center h-32 text-gray-400">
                No products available
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
