import React, { useState, useEffect } from 'react';
import './Cart.css';
import { useAuth } from './AuthContext';

const Cart = () => {
  const { user } = useAuth();
  const [cartItems, setCartItems] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadCart();
  }, []);

  const loadCart = async () => {
    try {
      setIsLoading(true);
      // Only try to load cart if user is logged in
      const endpoint = user?.email 
        ? `/api/customers/${user.email}/cart`
        : '/api/cart/guest'; // You might want to implement guest cart functionality
      
      const response = await fetch(endpoint);
      
      if (!response.ok) throw new Error('Failed to load cart');
      
      const data = await response.json();
      setCartItems(data.items || []);
      setError(null);
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  const removeItem = async (index) => {
    try {
      // Only allow removal if user is logged in
      if (!user?.email) {
        setError('Please login to modify your cart');
        return;
      }

      const response = await fetch(`/api/customers/${user.email}/cart/${index}`, {
        method: 'DELETE'
      });
      
      if (!response.ok) throw new Error('Failed to remove item');
      
      // Optimistic update
      setCartItems(prev => prev.filter((_, i) => i !== index));
    } catch (err) {
      setError(err.message);
      loadCart(); // Refresh cart if error occurs
    }
  };

  const calculateTotal = () => {
    return cartItems.reduce((total, item) => total + parseFloat(item.price || 0), 0).toFixed(2);
  };

  if (isLoading) return <div className="cart-container"><h2>Loading cart...</h2></div>;
  if (error) return <div className="cart-container"><h2>Error: {error}</h2></div>;

  return (
    <div className="cart-container">
      <h2>Your Cart</h2>
      
      {!user && (
        <div className="guest-notice">
          <p>You're browsing as a guest. <a href="/login">Login</a> to save your cart.</p>
        </div>
      )}
      
      {cartItems.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <>
          <div className="cart-items">
            {cartItems.map((item, index) => (
              <div className="cart-item" key={`${item._id}-${index}`}>
                <div className="cart-item-image">
                  <img 
                    src={item.productImageUrl} 
                    alt={item.productDescription} 
                    onError={(e) => {
                      e.target.onerror = null;
                      e.target.src = '/placeholder-image.png';
                    }}
                  />
                </div>
                <div className="cart-item-details">
                  <h3>{item.productDescription}</h3>
                  <p><strong>Brand:</strong> {item.brandName}</p>
                  <p className="price"><strong>Price:</strong> R{item.price}</p>
                  <p><strong>Store:</strong> {item.storeName}</p>
                  <div className="cart-item-actions">
                    {user?.email && (
                      <button 
                        onClick={() => removeItem(index)}
                        className="remove-item"
                      >
                        Remove
                      </button>
                    )}
                    <a
                      href={`https://${item.storeName}.com`}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="go-buy"
                    >
                      Buy Now
                    </a>
                  </div>
                </div>
              </div>
            ))}
          </div>
          <div className="cart-summary">
            <h3>Order Summary</h3>
            <div className="summary-row">
              <span>Subtotal ({cartItems.length} items)</span>
              <span>R{calculateTotal()}</span>
            </div>
            <div className="summary-row">
              <span>Estimated Delivery</span>
              <span>Varies by store</span>
            </div>
            <div className="summary-row total">
              <span>Total</span>
              <span>R{calculateTotal()}</span>
            </div>
            <div className="checkout-note">
              <p>To complete your purchase, please visit the individual store websites using the "Buy Now" buttons.</p>
              {!user && (
                <p className="login-prompt">
                  <a href="/login">Login</a> to save your cart for later.
                </p>
              )}
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
