import React, { useState } from "react";
import { toast } from "react-toastify";
import "./ProductCards.css";

function ProductCards({ products, itemsPerPage = 24, onAddToCart }) {
  const [currentPage, setCurrentPage] = useState(1);
  const [addingItem, setAddingItem] = useState(false);
  const totalPages = Math.ceil(products.length / itemsPerPage);

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  const handlePrevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const handleAddToCart = async (product) => {
    if (!onAddToCart) {
        toast.error('Cart functionality not available');
        return;
    }
    
    setAddingItem(true);
    try {
        const cartItem = {
            productId: product._id?.toString() || `temp-${Date.now()}`,
            productDescription: product.productDescription || 'Unknown product',
            brandName: product.brandName || 'Unknown brand',
            price: parseFloat(product.price?.replace(/[^0-9.]/g, '')) || 0,
            storeName: product.storeName || 'Unknown store',
            productImageUrl: product.productImageUrl || '',
            addedAt: new Date().toISOString()
        };
        
        await onAddToCart(cartItem);
        toast.success(`${cartItem.productDescription} added to cart!`);
    } catch (error) {
        console.error('Detailed add-to-cart error:', {
            error,
            product,
            time: new Date().toISOString()
        });
        
        const errorMessage = error.response?.data?.error 
            || error.message 
            || 'Failed to add to cart';
            
        toast.error(errorMessage.includes('JSON') 
            ? 'Server response error' 
            : errorMessage);
    } finally {
        setAddingItem(false);
    }
};
  const startIndex = (currentPage - 1) * itemsPerPage;
  const selectedProducts = products.slice(startIndex, startIndex + itemsPerPage);

  return (
    <div className="product-cards-container">
      <div className="product-cards">
        {selectedProducts.map((product) => (
          <div className="card" key={product._id}>
            <div className="card-content">
              <h3>{product.productDescription}</h3>
              <p>
                <strong>Brand:</strong> <span className="brand-name">{product.brandName}</span>
              </p>
              <p className="price">
                <strong>Price:</strong> {product.price}
              </p>
              <p>
                <strong>Store:</strong> {product.storeName}
              </p>
              <p><strong>Available since:</strong> {product.creationDate}</p>
              <div className="buttons">
                <button 
                  className="add-to-cart"
                  onClick={() => handleAddToCart(product)}
                  disabled={addingItem}
                >
                  {addingItem ? 'Adding...' : 'Add to Cart'}
                </button>
                <a
                  href={`https://${product.storeName}.com`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="go-buy"
                >
                  Go Buy
                </a>
              </div>
            </div>
            <ImageWithFallback
              src={product.productImageUrl}
              alt={product.productDescription}
              fallback={<div className="loading-spinner"></div>}
            />
          </div>
        ))}
      </div>
      
      <div className="pagination">
        <button className='navigation-btn' onClick={handlePrevPage} disabled={currentPage === 1}>
          Prev
        </button>
        <span>Page {currentPage} of {totalPages}</span>
        <button className='navigation-btn' onClick={handleNextPage} disabled={currentPage === totalPages}>
          Next
        </button>
      </div>
    </div>
  );
}

function ImageWithFallback({ src, alt, fallback }) {
  const [isError, setIsError] = useState(false);

  return (
    <div className="image-container">
      {!isError ? (
        <img
          src={src}
          alt={alt}
          className="product-image"
          onError={() => setIsError(true)}
        />
      ) : (
        fallback
      )}
    </div>
  );
}

export default ProductCards;
