import React, { useState, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import { Search, ShoppingCart, TrendingDown, Package, Store, AlertCircle } from 'lucide-react';

export default function Dashboard() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedStore, setSelectedStore] = useState('All');
  const [isScraping, setIsScraping] = useState(false);
  
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const response = await fetch('/api/dbproducts/getAll');
        
        if (!response.ok) {
          throw new Error('Failed to fetch products');
        }
        
        const data = await response.json();
        setProducts(data);
      } catch (err) {
        console.error('Error fetching products:', err);
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    
    fetchProducts();
  }, []);

  const handleSearch = async () => {
    if (!searchTerm.trim()) return;
    
    try {
      setIsScraping(true);
      setLoading(true);
      const response = await fetch(`/api/dbproducts/search?term=${encodeURIComponent(searchTerm)}`);
      
      if (!response.ok) {
        throw new Error('Search failed');
      }
      
      const data = await response.json();
      setProducts(data);
    } catch (err) {
      console.error('Search error:', err);
      setError(err.message);
    } finally {
      setIsScraping(false);
      setLoading(false);
    }
  };
  
  const productsByLowestPrice = React.useMemo(() => {
    const productMap = {};
    
    products.forEach(product => {
      if (!productMap[product._id]) {
        productMap[product._id] = product;
      } else if (parseFloat(product.price) < parseFloat(productMap[product._id].price)) {
        productMap[product._id] = product;
      }
    });
    
    return Object.values(productMap);
  }, [products]);
  
  const stores = React.useMemo(() => {
    const storeSet = new Set();
    products.forEach(product => {
      if (product.storeName) {
        storeSet.add(product.storeName);
      }
    });
    return ['All', ...Array.from(storeSet)];
  }, [products]);
  
  const filteredProducts = React.useMemo(() => {
    return products.filter(product => {
      const matchesSearch = product.productDescription?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                           product.brandName?.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesStore = selectedStore === 'All' || product.storeName === selectedStore;
      return matchesSearch && matchesStore;
    });
  }, [products, searchTerm, selectedStore]);
  
  const chartData = React.useMemo(() => {
    const productPrices = {};
    
    filteredProducts.forEach(product => {
      if (!productPrices[product._id]) {
        productPrices[product._id] = {
          productId: product._id,
          productName: product.productDescription?.length > 20 
            ? product.productDescription.substring(0, 20) + '...' 
            : product.productDescription,
        };
      }
      
      productPrices[product._id][product.storeName] = parseFloat(product.price);
    });
    
    return Object.values(productPrices).slice(0, 6);
  }, [filteredProducts]);
  
  const priceStats = React.useMemo(() => {
    if (filteredProducts.length === 0) return { avg: 0, min: 0, max: 0 };
    
    const prices = filteredProducts.map(p => parseFloat(p.price)).filter(p => !isNaN(p));
    const sum = prices.reduce((a, b) => a + b, 0);
    
    return {
      avg: prices.length ? (sum / prices.length).toFixed(2) : 0,
      min: prices.length ? Math.min(...prices).toFixed(2) : 0,
      max: prices.length ? Math.max(...prices).toFixed(2) : 0
    };
  }, [filteredProducts]);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-lg">
          {isScraping ? 'Scraping for products...' : 'Loading products...'}
        </div>
      </div>
    );
  }
  
  if (error) {
    return (
      <div className="flex flex-col items-center justify-center h-screen">
        <AlertCircle size={48} className="text-red-500 mb-4" />
        <div className="text-lg text-red-500">Error: {error}</div>
        <button 
          onClick={() => setError(null)}
          className="mt-4 px-4 py-2 bg-blue-500 text-white rounded"
        >
          Try Again
        </button>
      </div>
    );
  }
  
  return (
    <div className="p-6 max-w-full">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-2xl font-bold mb-2">SmartPrices Dashboard</h1>
        <p className="text-gray-600">Find the best deals across multiple stores</p>
      </div>
      
      {/* Search and Filter */}
      <div className="flex flex-wrap gap-4 mb-8">
        <div className="flex bg-white rounded shadow-sm items-center px-3 py-2 w-full md:w-auto flex-1">
          <Search className="mr-2 text-gray-400" size={20} />
          <input
            type="text"
            placeholder="Search products..."
            className="outline-none w-full"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
          />
          <button 
            onClick={handleSearch}
            className="ml-2 px-4 py-1 bg-blue-500 text-white rounded"
            disabled={isScraping}
          >
            {isScraping ? 'Searching...' : 'Search'}
          </button>
        </div>
        
        <select 
          className="rounded shadow-sm border border-gray-200 px-3 py-2"
          value={selectedStore}
          onChange={(e) => setSelectedStore(e.target.value)}
        >
          {stores.map(store => (
            <option key={store} value={store}>{store}</option>
          ))}
        </select>
      </div>
      
      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        {/* ... (keep your existing stats cards) ... */}
      </div>
      
      {/* Price Comparison Chart */}
      <div className="bg-white p-6 rounded shadow-sm mb-8">
        <h2 className="text-lg font-semibold mb-4">Price Comparison</h2>
        {chartData.length > 0 ? (
          <ResponsiveContainer width="100%" height={400}>
            <BarChart data={chartData}>
              {/* ... (keep your existing chart configuration) ... */}
            </BarChart>
          </ResponsiveContainer>
        ) : (
          <div className="flex justify-center items-center h-64 text-gray-400">
            {searchTerm ? 'No products found. Try a different search term.' : 'No products available'}
          </div>
        )}
      </div>
      
      {/* Best Deals Table */}
      <div className="bg-white p-6 rounded shadow-sm">
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-lg font-semibold">Best Deals</h2>
          <ShoppingCart size={20} className="text-gray-400" />
        </div>
        
        {productsByLowestPrice.length > 0 ? (
          <div className="overflow-x-auto">
            <table className="min-w-full table-auto">
              {/* ... (keep your existing table structure) ... */}
            </table>
          </div>
        ) : (
          <div className="flex justify-center items-center h-32 text-gray-400">
            No products available
          </div>
        )}
      </div>
    </div>
  );
}
