import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);
  const [cart, setCart] = useState([]);
  const [cartLoading, setCartLoading] = useState(false);

  const navigate = useNavigate();

  const loadCartItems = async (token, email) => {
    setCartLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:8081/api/customers/${email}/cart`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setCart(response.data.items || []);
      localStorage.setItem('cart', JSON.stringify(response.data.items || []));
    } catch (error) {
      console.error('Failed to load cart:', error);
      setCart([]);
    } finally {
      setCartLoading(false);
    }
  };

  useEffect(() => {
    const initializeAuth = async () => {
      const token = localStorage.getItem('token');
      const userData = localStorage.getItem('user');
      
      if (token && userData) {
        try {
          const response = await axios.get('http://localhost:8081/auth/verify', {
            headers: { Authorization: `Bearer ${token}` }
          });
          
          if (response.data.valid) {
            const parsedUser = JSON.parse(userData);
            setIsAuthenticated(true);
            setUser(parsedUser);
            await loadCartItems(token, parsedUser.email);
          } else {
            clearAuth();
          }
        } catch (error) {
          clearAuth();
        }
      }
      setLoading(false);
    };
    
    initializeAuth();
  }, []);

  const login = async (token, userData) => {
    localStorage.setItem('token', token);
    localStorage.setItem('user', JSON.stringify(userData));
    setIsAuthenticated(true);
    setUser(userData);
    await loadCartItems(token, userData.email);
  };

  const logout = () => {
    clearAuth();
    navigate('/login');
  };

  const clearAuth = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    localStorage.removeItem('cart');
    setIsAuthenticated(false);
    setUser(null);
    setCart([]);
  };

  const addToCart = async (item) => {
    if (!user) return false;
    
    try {
      setCartLoading(true);
      const response = await axios.post(
        `http://localhost:8081/api/customers/${user.email}/cart`,
        item,
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );
      
      await loadCartItems(localStorage.getItem('token'), user.email);
      return true;
    } catch (error) {
      console.error('Failed to add to cart:', error);
      return false;
    } finally {
      setCartLoading(false);
    }
  };

  const removeFromCart = async (index) => {
    if (!user) return false;
    
    try {
      setCartLoading(true);
      await axios.delete(
        `http://localhost:8081/api/customers/${user.email}/cart/${index}`,
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );
      
      await loadCartItems(localStorage.getItem('token'), user.email);
      return true;
    } catch (error) {
      console.error('Failed to remove from cart:', error);
      return false;
    } finally {
      setCartLoading(false);
    }
  };

  return (
    <AuthContext.Provider value={{ 
      isAuthenticated, 
      user,
      cart,
      loading,
      cartLoading,
      login,
      logout,
      addToCart,
      removeFromCart,
      loadCartItems
    }}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  return useContext(AuthContext);
}
