import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from './AuthContext';
import './Login.css';

function Login() {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });

  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();
  const { login } = useAuth();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.id]: e.target.value });
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');
    setMessage('');

    try {
      const response = await axios.post('http://localhost:8081/auth/login', formData);
      
      // Enhanced response validation
      if (!response?.data?.token || !response?.data?.user?.email) {
        throw new Error('Invalid server response structure');
      }

      console.log('Login successful - Response:', response.data);
      
      // Store authentication data
      login(response.data.token, response.data.user);
      setMessage('Login successful! Redirecting...');

      // Determine redirect path with fallbacks
      const redirectPath = location.state?.from?.pathname || 
                         localStorage.getItem('preAuthPath') || 
                         '/home';
      
      console.log('Redirecting to:', redirectPath);
      
      // Clear any stored pre-auth path
      localStorage.removeItem('preAuthPath');

      // Immediate feedback before navigation
      await new Promise(resolve => setTimeout(resolve, 1500));
      
      navigate(redirectPath, { replace: true });

    } catch (err) {
      setIsLoading(false);
      
      // Enhanced error handling
      if (err.response) {
        const serverError = err.response.data?.message || err.response.data;
        console.error('Login error:', serverError);
        setError(serverError || 'Invalid credentials');
      } else if (err.request) {
        console.error('Network error:', err.request);
        setError('Network error. Please check your connection.');
      } else {
        console.error('Login error:', err.message);
        setError(err.message || 'Login failed');
      }
    } finally {
      if (!error) {
        setIsLoading(false);
      }
    }
  };

  const handleGoogleSignIn = () => {
    const redirectPath = location.state?.from?.pathname || '/home';
    localStorage.setItem('preAuthPath', redirectPath);
    console.log('Initiating Google OAuth with redirect to:', redirectPath);
    window.location.href = "https://accounts.google.com/o/oauth2/auth?...";
  };

  return (
    <section className="signup-container">
      {/* Content Section */}
      <div className="content-section">
        <div className="content-wrapper">
          <img src="images/smileShop.png" alt="ZAPriceFinder Logo" className="brand-logo" />
          <h1><span>ZAPriceFinder!!!</span> Making your online shopping easier.</h1>
          <p className="section-paragraph">Find the best prices across multiple stores in seconds.</p>
        </div>
      </div>

      {/* Login Form Section */}
      <div className="form-section">
        <div className="form-wrapper">
          <h2>Great to have you back!</h2>
          <br /><br />
          
          <form onSubmit={handleLogin}>
            <div className="input-container">
              <div className="form-group">
                <label htmlFor="email">Email</label>
                <input 
                  placeholder="Enter your email" 
                  type="email" 
                  id="email" 
                  value={formData.email}
                  onChange={handleChange}
                  aria-autocomplete="off" 
                  required
                  disabled={isLoading}
                />
              </div>
            </div>
            <div className="input-container">
              <div className="form-group">
                <label htmlFor="password">Password</label>
                <input 
                  placeholder="Enter password" 
                  type="password" 
                  id="password" 
                  value={formData.password}
                  onChange={handleChange}
                  required
                  disabled={isLoading}
                />
              </div>
            </div>
            <div className="remember-forgot">
              <div className="remember-me">
                <input 
                  type="checkbox" 
                  id="remember-me" 
                  disabled={isLoading}
                />
                <label htmlFor="remember-me">Remember me</label>
              </div>
              <a className='blue' href="#">Forgot password?</a>
            </div>

            <button 
              type="submit" 
              className="login-btn"
              disabled={isLoading}
            >
              {isLoading ? 'Logging in...' : 'Login'}
            </button>
          </form>

          {error && (
            <p className="error-message">
              {typeof error === 'object' ? JSON.stringify(error) : error}
            </p>
          )}
          {message && <p className="success-message">{message}</p>}

          <div className="divider">or</div>

          <button 
            className="google-signin" 
            onClick={handleGoogleSignIn}
            disabled={isLoading}
          >
            <img src="/images/googleSVG.png" alt="Google Logo" className="google-icon" />
            <span>Sign in with Google</span>
          </button>
        </div>
      </div>
    </section>
  );
}

export default Login;
