import React, { useEffect } from 'react';
import './Cart.css';
import { useAuth } from './AuthContext';
import { toast } from 'react-toastify';

const Cart = () => {
  const { 
    user, 
    isAuthenticated, 
    cart, 
    cartLoading, 
    removeFromCart,
    loadCartItems
  } = useAuth();

  useEffect(() => {
    if (isAuthenticated && user) {
      loadCartItems(localStorage.getItem('token'), user.email);
    }
  }, [isAuthenticated, user, loadCartItems]);

  const handleRemoveItem = async (index) => {
    const success = await removeFromCart(index);
    if (success) {
      toast.success('Item removed from cart');
    } else {
      toast.error('Failed to remove item');
    }
  };

  const calculateTotal = () => {
    return cart.reduce((total, item) => total + parseFloat(item.price || 0), 0).toFixed(2);
  };

  if (cartLoading) return <div className="cart-container"><h2>Loading cart...</h2></div>;

  return (
    <div className="cart-container">
      <h2>Your Cart</h2>
      
      {cart.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <>
          <div className="cart-items">
            {cart.map((item, index) => (
              <div className="cart-item" key={`${item.productId}-${index}`}>
                <div className="cart-item-image">
                  <img 
                    src={item.productImageUrl} 
                    alt={item.productDescription}
                    onError={(e) => {
                      e.target.onerror = null;
                      e.target.src = '/placeholder-image.png';
                    }}
                  />
                </div>
                <div className="cart-item-details">
                  <h3>{item.productDescription}</h3>
                  <p><strong>Brand:</strong> {item.brandName}</p>
                  <p className="price"><strong>Price:</strong> R{item.price}</p>
                  <p><strong>Store:</strong> {item.storeName}</p>
                  <div className="cart-item-actions">
                    <button 
                      onClick={() => handleRemoveItem(index)}
                      disabled={cartLoading}
                    >
                      {cartLoading ? 'Removing...' : 'Remove'}
                    </button>
                    <a
                      href={`https://${item.storeName}.com`}
                      target="_blank"
                      rel="noopener noreferrer"
                    >
                      Buy Now
                    </a>
                  </div>
                </div>
              </div>
            ))}
          </div>
          <div className="cart-summary">
            <h3>Order Summary</h3>
            <div className="summary-row">
              <span>Subtotal ({cart.length} items)</span>
              <span>R{calculateTotal()}</span>
            </div>
            <div className="summary-row total">
              <span>Total</span>
              <span>R{calculateTotal()}</span>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
