import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';

const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);
  const [cart, setCart] = useState([]);
  const [cartLoading, setCartLoading] = useState(false);

  useEffect(() => {
    const initializeAuth = async () => {
      const token = localStorage.getItem('token');
      const userData = localStorage.getItem('user');
      const savedCart = localStorage.getItem('cart');
      
      if (token && userData) {
        try {
          const response = await axios.get('http://localhost:8081/auth/verify', {
            headers: { Authorization: `Bearer ${token}` }
          });
          
          if (response.data.valid) {
            const parsedUser = JSON.parse(userData);
            setIsAuthenticated(true);
            setUser(parsedUser);
            
            // Initialize cart from localStorage or fetch from server
            if (savedCart) {
              setCart(JSON.parse(savedCart));
            } else {
              await loadCartItems(token, parsedUser.email);
            }
          } else {
            clearAuth();
          }
        } catch (error) {
          clearAuth();
        }
      }
      setLoading(false);
    };
    
    initializeAuth();
  }, []);

  const loadCartItems = async (token, email) => {
    setCartLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:8081/api/customers/${email}/cart`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const cartItems = response.data.items || [];
      setCart(cartItems);
      localStorage.setItem('cart', JSON.stringify(cartItems));
    } catch (error) {
      console.error('Failed to load cart:', error);
    } finally {
      setCartLoading(false);
    }
  };

  const login = async (token, userData) => {
    try {
      if (!userData?.email) {
        throw new Error('Invalid user data received');
      }

      localStorage.setItem('token', token);
      localStorage.setItem('user', JSON.stringify(userData));
      setIsAuthenticated(true);
      setUser(userData);
      
      await loadCartItems(token, userData.email);
      return true;
    } catch (error) {
      console.error('Login error:', error);
      clearAuth();
      throw error;
    }
  };

  const logout = () => {
    clearAuth();
    toast.success('Logged out successfully');
  };

  const clearAuth = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    localStorage.removeItem('cart');
    setIsAuthenticated(false);
    setUser(null);
    setCart([]);
  };

  const updateCart = async (cartItems) => {
    const token = localStorage.getItem('token');
    const userData = JSON.parse(localStorage.getItem('user') || 'null');
    
    if (!token || !userData?.email) {
      throw new Error('Please login to update your cart');
    }

    try {
      setCartLoading(true);
      const response = await axios.post(
        `http://localhost:8081/api/customers/${userData.email}/cart/sync`,
        { items: cartItems },
        { 
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      if (response.status === 200) {
        setCart(cartItems);
        localStorage.setItem('cart', JSON.stringify(cartItems));
        return true;
      }
      throw new Error('Failed to update cart');
    } catch (error) {
      console.error('Cart update failed:', error);
      throw error;
    } finally {
      setCartLoading(false);
    }
  };

  const addToCart = async (item) => {
    const newCart = [...cart, item];
    return await updateCart(newCart);
  };

  const removeFromCart = async (index) => {
    const newCart = [...cart];
    newCart.splice(index, 1);
    return await updateCart(newCart);
  };

  return (
    <AuthContext.Provider value={{ 
      isAuthenticated,
      user,
      loading,
      cart,
      cartLoading,
      login,
      logout,
      addToCart,
      removeFromCart,
      updateCart,
      loadCartItems
    }}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}
