import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Home from './Home';
import HotDeals from './HotDeals';
import Cart from './Cart';
import Dashboard from './Dashboard';
import Signup from './Signup'; // Your standalone Signup component
import Login from './Login'; // Your standalone Login component
import { AuthProvider, useAuth } from './AuthContext';

function App() {
  return (
    <AuthProvider>
      <Router>
        <Routes>
          {/* Make Signup the default route */}
          <Route path="/" element={<Signup />} />
          
          {/* Home route now has its own path */}
          <Route path="/home" element={<Home />} />
          
          <Route path="/hot" element={<HotDeals />} />
          <Route path="/cart" element={<Cart />} />
          <Route path="/dashboard" element={
            <ProtectedRoute>
              <Dashboard />
            </ProtectedRoute>
          } />
          <Route path="/login" element={<Login />} />
          <Route path="/signup" element={<Signup />} />
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </Router>
    </AuthProvider>
  );
}

function ProtectedRoute({ children }) {
  const { isAuthenticated } = useAuth();
  return isAuthenticated ? children : <Navigate to="/login" />;
}

export default App;
