import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);
  const [cart, setCart] = useState(() => {
    // Initialize cart from localStorage if available
    const savedCart = localStorage.getItem('cart');
    return savedCart ? JSON.parse(savedCart) : [];
  });

  useEffect(() => {
    const initializeAuth = async () => {
      const token = localStorage.getItem('token');
      const userData = localStorage.getItem('user');
      
      if (token && userData) {
        try {
          const response = await axios.get('http://localhost:8081/auth/verify', {
            headers: { Authorization: `Bearer ${token}` }
          });
          
          if (response.data.valid) {
            setIsAuthenticated(true);
            const parsedUser = JSON.parse(userData);
            setUser(parsedUser);
            
            // Load cart for authenticated user
            try {
              const cartResponse = await axios.get(
                `http://localhost:8081/api/customers/${parsedUser.email}/cart`,
                { headers: { Authorization: `Bearer ${token}` } }
              );
              setCart(cartResponse.data.items || []);
            } catch (cartError) {
              console.error('Failed to load cart:', cartError);
            }
          } else {
            clearAuth();
          }
        } catch (error) {
          clearAuth();
        }
      }
      setLoading(false);
    };
    
    initializeAuth();
  }, []);

  const login = async (token, userData) => {
    // 1. Validate response structure
    if (!userData || typeof userData !== 'object') {
      console.error('Invalid user data received:', userData);
      throw new Error('Invalid user data from server');
    }
  
    if (!userData.email) {
      console.error('User data missing email:', userData);
      throw new Error('User email is required');
    }
  
    // 2. Store auth data
    localStorage.setItem('token', token);
    localStorage.setItem('user', JSON.stringify(userData));
    setIsAuthenticated(true);
    setUser(userData);
    
    // 3. Load cart with error handling
    try {
      console.log('Loading cart for:', userData.email); // Debug log
      
      const response = await axios.get(
        `http://localhost:8081/api/customers/${userData.email}/cart`,
        { 
          headers: { Authorization: `Bearer ${token}` },
          transformResponse: [], // Prevent auto-parsing
          responseType: 'json'
        }
      );
  
      // Handle potential string response
      const responseData = typeof response.data === 'string' 
        ? JSON.parse(response.data)
        : response.data;
  
      const cartItems = responseData?.items || [];
      setCart(cartItems);
      localStorage.setItem('cart', JSON.stringify(cartItems));
      
    } catch (error) {
      console.error('Cart load error:', {
        error: error.response?.data || error.message,
        status: error.response?.status
      });
      // Initialize empty cart on error
      setCart([]);
      localStorage.setItem('cart', JSON.stringify([]));
    }
  };
  const logout = () => {
    clearAuth();
  };

  const clearAuth = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    localStorage.removeItem('cart');
    setIsAuthenticated(false);
    setUser(null);
    setCart([]);
  };

  const loadCartItems = async (token, email) => {
    setCartLoading(true);
    try {
      const response = await axios.get(`/api/customers/${email}/cart`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setCart(response.data.items || []);
    } catch (error) {
      console.error('Failed to load cart:', error);
      setCart([]);
    } finally {
      setCartLoading(false);
    }
  };

  const addToCart = async (token, email, item) => {
    try {
      await axios.post(`/api/customers/${email}/cart`, item, {
        headers: { Authorization: `Bearer ${token}` }
      });
      await loadCartItems(token, email); // Refresh cart
      return true;
    } catch (error) {
      console.error('Failed to add to cart:', error);
      return false;
    }
  };

  const removeFromCart = async (token, email, index) => {
    try {
      await axios.delete(`/api/customers/${email}/cart/${index}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      await loadCartItems(token, email); // Refresh cart
      return true;
    } catch (error) {
      console.error('Failed to remove from cart:', error);
      return false;
    }
  };

  const clearCart = async (token, email) => {
    try {
      await axios.delete(`/api/customers/${email}/cart`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setCart([]);
      return true;
    } catch (error) {
      console.error('Failed to clear cart:', error);
      return false;
    }
  };

  return (
    <AuthContext.Provider value={{
      isAuthenticated,
      user,
      loading,
      cartart,
      addToCart, // Make sure this is exposed
      updateCart,
      login,
      logout
    }}>
      {children}
    </AuthContext.Provider>
  );
}


export function useAuth() {
  return useContext(AuthContext);
}
