import React, { useEffect } from 'react';
import { useAuth } from './AuthContext';
import { toast } from 'react-toastify';
import './Cart.css';

const Cart = () => {
  const { 
    isAuthenticated,
    user,
    cart ,
    cartLoading,
    cartError,
    loadCartItems,
    removeFromCart
  } = useAuth();

  // Load cart on mount and when auth changes
  useEffect(() => {
    if (isAuthenticated && user?.email) {
      const token = localStorage.getItem('token');
      loadCartItems(token, user.email);
    }
  }, [isAuthenticated, user, loadCartItems]);

  // Handle errors
  useEffect(() => {
    if (cartError) {
      toast.error(cartError);
    }
  }, [cartError]);

  const handleRemoveItem = async (index) => {
    if (cartLoading) return;
    
    const token = localStorage.getItem('token');
    const email = user?.email;
    
    if (!token || !email) {
      toast.error('Please login to modify your cart');
      return;
    }

    try {
      await removeFromCart(token, email, index);
      toast.success('Item removed from cart');
    } catch (error) {
      toast.error('Failed to remove item');
    }
  };

  const calculateTotal = () => {
    return cart.reduce((total, item) => {
      const price = typeof item.price === 'string' 
        ? parseFloat(item.price.replace(/[^0-9.]/g, '')) 
        : item.price || 0;
      return total + price;
    }, 0).toFixed(2);
  };

  if (!isAuthenticated) {
    return (
      <div className="cart-container">
        <h2>Your Cart</h2>
        <p>Please login to view your cart</p>
      </div>
    );
  }

  if (cartLoading && cart.length === 0) {
    return (
      <div className="cart-container">
        <h2>Your Cart</h2>
        <div className="loading-spinner"></div>
      </div>
    );
  }

  return (
    <div className="cart-container">
      <h2>Your Cart</h2>
      
      {cart.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <>
          <div className="cart-items">
            {cart.map((item, index) => (
              <div className={`cart-item ${cartLoading ? 'updating' : ''}`} 
                   key={`${item.productId}-${index}`}>
                <div className="cart-item-image">
                  <img 
                    src={item.productImageUrl} 
                    alt={item.productDescription}
                    onError={(e) => {
                      e.target.onerror = null;
                      e.target.src = '/placeholder-image.png';
                    }}
                  />
                </div>
                <div className="cart-item-details">
                  <h3>{item.productDescription}</h3>
                  <p><strong>Brand:</strong> {item.brandName}</p>
                  <p className="price">
                    <strong>Price:</strong> R{typeof item.price === 'number' ? item.price.toFixed(2) : item.price}
                  </p>
                  <p><strong>Store:</strong> {item.storeName}</p>
                  <div className="cart-item-actions">
                    <button 
                      onClick={() => handleRemoveItem(index)}
                      disabled={cartLoading}
                    >
                      {cartLoading ? 'Removing...' : 'Remove'}
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
          
          <div className="cart-summary">
            <h3>Order Summary</h3>
            <div className="summary-row">
              <span>Subtotal ({cart.length} items)</span>
              <span>R{calculateTotal()}</span>
            </div>
            <div className="summary-row total">
              <span>Total</span>
              <span>R{calculateTotal()}</span>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
