import React, { useState, useEffect } from 'react';
import './Cart.css';
import { useAuth } from './AuthContext';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const Cart = () => {
  const { user, isAuthenticated, updateCart } = useAuth();
  const navigate = useNavigate();
  const [cartItems, setCartItems] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/login', { state: { from: '/cart' }, replace: true });
      return;
    }
    
    const loadCart = async () => {
      try {
        setIsLoading(true);
        const response = await axios.get(
          `http://localhost:8081/api/customers/${user.email}/cart`,
          {
            headers: { 
              Authorization: `Bearer ${localStorage.getItem('token')}` 
            }
          }
        );
        
        setCartItems(response.data.items || []);
        setError(null);
      } catch (err) {
        setError(err.response?.data?.message || 'Failed to load cart');
      } finally {
        setIsLoading(false);
      }
    };
    
    loadCart();
  }, [isAuthenticated, navigate, user]);

  const removeItem = async (index) => {
    try {
      // Optimistic update
      const newItems = [...cartItems];
      newItems.splice(index, 1);
      setCartItems(newItems);
      
      // Sync with backend
      await axios.delete(
        `http://localhost:8081/api/customers/${user.email}/cart/${index}`,
        {
          headers: { 
            Authorization: `Bearer ${localStorage.getItem('token')}` 
          }
        }
      );
      
      // Update global cart state
      updateCart(newItems);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to remove item');
      // Revert on error
      const response = await axios.get(
        `http://localhost:8081/api/customers/${user.email}/cart`,
        {
          headers: { 
            Authorization: `Bearer ${localStorage.getItem('token')}` 
          }
        }
      );
      setCartItems(response.data.items || []);
    }
  };

  const calculateTotal = () => {
    return cartItems.reduce((total, item) => total + parseFloat(item.price || 0), 0).toFixed(2);
  };

  if (isLoading) return <div className="cart-container"><h2>Loading cart...</h2></div>;
  if (error) return <div className="cart-container"><h2>Error: {error}</h2></div>;

  return (
    <div className="cart-container">
      <h2>Your Cart</h2>
      
      {cartItems.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <>
          <div className="cart-items">
            {cartItems.map((item, index) => (
              <div className="cart-item" key={`${item._id}-${index}`}>
                <div className="cart-item-image">
                  <img 
                    src={item.productImageUrl} 
                    alt={item.productDescription} 
                    onError={(e) => {
                      e.target.onerror = null;
                      e.target.src = '/placeholder-image.png';
                    }}
                  />
                </div>
                <div className="cart-item-details">
                  <h3>{item.productDescription}</h3>
                  <p><strong>Brand:</strong> {item.brandName}</p>
                  <p className="price"><strong>Price:</strong> R{item.price}</p>
                  <p><strong>Store:</strong> {item.storeName}</p>
                  <div className="cart-item-actions">
                    <button 
                      onClick={() => removeItem(index)}
                      className="remove-item"
                    >
                      Remove
                    </button>
                    <a
                      href={`https://${item.storeName}.com`}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="go-buy"
                    >
                      Buy Now
                    </a>
                  </div>
                </div>
              </div>
            ))}
          </div>
          <div className="cart-summary">
            <h3>Order Summary</h3>
            <div className="summary-row">
              <span>Subtotal ({cartItems.length} items)</span>
              <span>R{calculateTotal()}</span>
            </div>
            <div className="summary-row">
              <span>Estimated Delivery</span>
              <span>Varies by store</span>
            </div>
            <div className="summary-row total">
              <span>Total</span>
              <span>R{calculateTotal()}</span>
            </div>
            <div className="checkout-note">
              <p>To complete your purchase, please visit the individual store websites using the "Buy Now" buttons.</p>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
