import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from './AuthContext';
import { toast } from 'react-toastify';
import './Login.css';

function Login() {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });

  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();
  const { login } = useAuth();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.id]: e.target.value });
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    
    try {
      const response = await axios.post('http://localhost:8081/auth/login', formData);
      
      // Store user data and token using AuthContext
      const loginSuccess = await login(response.data.token, response.data.user);
      
      if (loginSuccess) {
        toast.success('Login successful!');
        const from = location.state?.from || '/home';
        navigate(from, { replace: true });
      }
    } catch (err) {
      let errorMessage = 'Login failed';
      
      if (err.response) {
        errorMessage = err.response.data.message || 'Invalid credentials';
      } else if (err.request) {
        errorMessage = 'No response from server. Please check your connection.';
      }
      
      toast.error(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const handleGoogleSignIn = () => {
    localStorage.setItem('preAuthPath', location.state?.from || '/home');
    window.location.href = "https://accounts.google.com/o/oauth2/auth?...";
  };

  return (
    <section className="signup-container">
      {/* Content Section */}
      <div className="content-section">
        <div className="content-wrapper">
          <img src="images/smileShop.png" alt="ZAPriceFinder Logo" className="brand-logo" />
          <h1><span>ZAPriceFinder!!!</span> Making your online shopping easier.</h1>
          <p className="section-paragraph">Find the best prices across multiple stores in seconds.</p>
        </div>
      </div>

      {/* Login Form Section */}
      <div className="form-section">
        <div className="form-wrapper">
          <h2>Great to have you back!</h2>
          <br /><br />
          
          <form onSubmit={handleLogin}>
            <div className="input-container">
              <div className="form-group">
                <label htmlFor="email">Email</label>
                <input 
                  placeholder="Enter your email" 
                  type="email" 
                  id="email" 
                  value={formData.email}
                  onChange={handleChange}
                  aria-autocomplete="off" 
                  required
                />
              </div>
            </div>
            <div className="input-container">
              <div className="form-group">
                <label htmlFor="password">Password</label>
                <input 
                  placeholder="Enter password" 
                  type="password" 
                  id="password" 
                  value={formData.password}
                  onChange={handleChange}
                  required
                />
              </div>
            </div>
            <div className="remember-forgot">
              <div className="remember-me">
                <input type="checkbox" id="remember-me" />
                <label htmlFor="remember-me">Remember me</label>
              </div>
              <a className='blue' href="#">Forgot password?</a>
            </div>

            <button 
              type="submit" 
              className="login-btn"
              disabled={loading}
            >
              {loading ? 'Logging in...' : 'Login'}
            </button>
          </form>

          <div className="divider">or</div>

          <button 
            className="google-signin" 
            onClick={handleGoogleSignIn}
            disabled={loading}
          >
            <img src="/images/googleSVG.png" alt="Google Logo" className="google-icon" />
            <span>Sign in with Google</span>
          </button>
        </div>
      </div>
    </section>
  );
}

export default Login;
