import React, { useState, useEffect } from 'react';
import './Cart.css';

const Cart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadCart();
  }, []);

  const loadCart = async () => {
    try {
      setIsLoading(true);
      setError(null);
      
      // Load cart from localStorage for guest users
      const localCart = localStorage.getItem('cart');
      if (localCart) {
        setCartItems(JSON.parse(localCart));
        return;
      }
      
      setCartItems([]);
    } catch (err) {
      console.error('Cart loading error:', err);
      setError('Failed to load cart');
      setCartItems([]);
    } finally {
      setIsLoading(false);
    }
  };

  const removeItem = async (index) => {
    try {
      // Update local state
      const updatedCart = [...cartItems];
      updatedCart.splice(index, 1);
      setCartItems(updatedCart);
      
      // Save to localStorage
      localStorage.setItem('cart', JSON.stringify(updatedCart));
    } catch (err) {
      console.error('Remove item error:', err);
      setError('Failed to remove item');
    }
  };

  const calculateTotal = () => {
    return cartItems.reduce((total, item) => total + (Number(item.price) || 0), 0).toFixed(2);
  };

  if (isLoading) return <div className="cart-container"><h2>Loading cart...</h2></div>;
  if (error) return <div className="cart-container"><h2>Error: {error}</h2></div>;

  return (
    <div className="cart-container">
      <h2>Your Cart</h2>
      
      {cartItems.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <>
          <div className="cart-items">
            {cartItems.map((item, index) => (
              <div className="cart-item" key={`${index}`}>
                <div className="cart-item-image">
                  <img 
                    src={item.productImageUrl} 
                    alt={item.productDescription} 
                    onError={(e) => {
                      e.target.onerror = null;
                      e.target.src = '/placeholder-image.png';
                    }}
                  />
                </div>
                <div className="cart-item-details">
                  <h3>{item.productDescription}</h3>
                  <p><strong>Brand:</strong> {item.brandName}</p>
                  <p className="price"><strong>Price:</strong> R{item.price}</p>
                  <p><strong>Store:</strong> {item.storeName}</p>
                  <div className="cart-item-actions">
                    <button 
                      onClick={() => removeItem(index)}
                      className="remove-item"
                    >
                      Remove
                    </button>
                    <a
                      href={`https://${item.storeName}.com`}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="go-buy"
                    >
                      Buy Now
                    </a>
                  </div>
                </div>
              </div>
            ))}
          </div>
          <div className="cart-summary">
            <h3>Order Summary</h3>
            <div className="summary-row">
              <span>Subtotal ({cartItems.length} items)</span>
              <span>R{calculateTotal()}</span>
            </div>
            <div className="summary-row">
              <span>Estimated Delivery</span>
              <span>Varies by store</span>
            </div>
            <div className="summary-row total">
              <span>Total</span>
              <span>R{calculateTotal()}</span>
            </div>
            <div className="checkout-note">
              <p>To complete your purchase, please visit the individual store websites using the "Buy Now" buttons.</p>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
