import React, { useState } from "react";
import "./ProductCards.css";

function ProductCards({ products, itemsPerPage = 12 }) {
  const [currentPage, setCurrentPage] = useState(1);
  const totalPages = Math.ceil(products.length / itemsPerPage);

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  const handlePrevPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const startIndex = (currentPage - 1) * itemsPerPage;
  const selectedProducts = products.slice(startIndex, startIndex + itemsPerPage);

  return (
    <div className="product-cards-container">
      <div className="product-cards">
        {selectedProducts.map((product) => (
          <div className="card" key={product._id}>
            <div className="card-badge">{product.storeName}</div>
            <ImageWithFallback
              src={product.productImageUrl}
              alt={product.productDescription}
              fallback={<div className="image-placeholder"></div>}
            />
            <div className="card-content">
              <div className="card-header">
                <h3 className="product-title">{product.productDescription}</h3>
                <p className="brand-name">{product.brandName}</p>
              </div>
              <div className="card-footer">
                <p className="price">{product.price}</p>
                <div className="buttons">
                  <button className="add-to-cart">
                    <i className="fas fa-shopping-cart"></i> Add to Cart
                  </button>
                  <a
                    href={`https://${product.storeName}.com`}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="go-buy"
                  >
                    <i className="fas fa-external-link-alt"></i> View
                  </a>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
      
      {totalPages > 1 && (
        <div className="pagination">
          <button 
            className='pagination-btn' 
            onClick={handlePrevPage} 
            disabled={currentPage === 1}
          >
            <i className="fas fa-chevron-left"></i>
          </button>
          <span>Page {currentPage} of {totalPages}</span>
          <button 
            className='pagination-btn' 
            onClick={handleNextPage} 
            disabled={currentPage === totalPages}
          >
            <i className="fas fa-chevron-right"></i>
          </button>
        </div>
      )}
    </div>
  );
}

function ImageWithFallback({ src, alt, fallback }) {
  const [isError, setIsError] = useState(false);

  return (
    <div className="image-container">
      {!isError ? (
        <img
          src={src}
          alt={alt}
          className="product-image"
          onError={() => setIsError(true)}
          loading="lazy"
        />
      ) : (
        fallback
      )}
    </div>
  );
}

export default ProductCards;
