import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);
  const [cart, setCart] = useState(() => {
    // Initialize cart from localStorage if available
    const savedCart = localStorage.getItem('cart');
    return savedCart ? JSON.parse(savedCart) : [];
  });

  useEffect(() => {
    const initializeAuth = async () => {
      const token = localStorage.getItem('token');
      const userData = localStorage.getItem('user');
      
      if (token && userData) {
        try {
          const response = await axios.get('http://localhost:8081/auth/verify', {
            headers: { Authorization: `Bearer ${token}` }
          });
          
          if (response.data.valid) {
            setIsAuthenticated(true);
            const parsedUser = JSON.parse(userData);
            setUser(parsedUser);
            
            // Load cart for authenticated user
            try {
              const cartResponse = await axios.get(
                `http://localhost:8081/api/customers/${parsedUser.email}/cart`,
                { headers: { Authorization: `Bearer ${token}` } }
              );
              setCart(cartResponse.data.items || []);
            } catch (cartError) {
              console.error('Failed to load cart:', cartError);
            }
          } else {
            clearAuth();
          }
        } catch (error) {
          clearAuth();
        }
      }
      setLoading(false);
    };
    
    initializeAuth();
  }, []);

  const login = async (token, userData) => {
    localStorage.setItem('token', token);
    localStorage.setItem('user', JSON.stringify(userData));
    setIsAuthenticated(true);
    setUser(userData);
    
    // Load user's cart after login
    try {
      const response = await axios.get(
        `http://localhost:8081/api/customers/${userData.email}/cart`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const cartItems = response.data.items || [];
      setCart(cartItems);
      localStorage.setItem('cart', JSON.stringify(cartItems));
    } catch (error) {
      console.error('Failed to load cart:', error);
    }
  };

  const logout = () => {
    clearAuth();
  };

  const clearAuth = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    localStorage.removeItem('cart');
    setIsAuthenticated(false);
    setUser(null);
    setCart([]);
  };

  // In your AuthContext.js
const updateCart = async (cartItems) => {
  if (!user || !user.email) {
    toast.error('Please login to update cart');
    return;
  }

  try {
    // Optimistically update local state first
    setCart(cartItems);
    localStorage.setItem('cart', JSON.stringify(cartItems));

    // Sync with backend
    const token = localStorage.getItem('token');
    const response = await axios.post(
      `http://localhost:8081/api/customers/${user.email}/cart/sync`,
      { items: cartItems },
      { 
        headers: { 
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      }
    );

    if (response.status === 200) {
      toast.success('Cart updated successfully!');
    } else {
      throw new Error('Failed to sync cart');
    }
  } catch (error) {
    console.error('Cart sync error:', error);
    // Revert to last known good state
    const savedCart = localStorage.getItem('cart');
    setCart(savedCart ? JSON.parse(savedCart) : []);
    toast.error('Failed to update cart. Please try again.');
  }
};

const addToCart = async (item) => {
  const newCart = [...cart, item];
  await updateCart(newCart);
};

  
}

export function useAuth() {
  return useContext(AuthContext);
}
