import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';

const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);
  const [cart, setCart] = useState([]);
  const [cartLoading, setCartLoading] = useState(false);

  useEffect(() => {
    const initializeAuth = async () => {
      const token = localStorage.getItem('token');
      const userData = localStorage.getItem('user');
      
      if (token && userData) {
        try {
          const response = await axios.get('http://localhost:8081/auth/verify', {
            headers: { Authorization: `Bearer ${token}` }
          });
          
          // Check both the valid flag and successful response
          if (response.data?.valid === true) {
            setIsAuthenticated(true);
            const parsedUser = JSON.parse(userData);
            setUser(parsedUser);
            
            // Load cart for authenticated user
            try {
              await loadCartItems(token, parsedUser.email);
            } catch (cartError) {
              console.error('Failed to load cart:', cartError);
              // Don't fail auth if cart fails to load
            }
          } else {
            clearAuth();
          }
        } catch (error) {
          console.error('Token verification failed:', error);
          clearAuth();
        }
      }
      setLoading(false);
    };
    
    initializeAuth();
  }, []);

  const loadCartItems = async (token, email) => {
    setCartLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:8081/api/customers/${email}/cart`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const cartItems = response.data.items || [];
      setCart(cartItems);
      localStorage.setItem('cart', JSON.stringify(cartItems));
    } catch (error) {
      console.error('Failed to load cart:', error);
      throw error;
    } finally {
      setCartLoading(false);
    }
  };

  const login = async (token, userData) => {
    try {
      // First verify the token is valid
      const verifyResponse = await axios.get('http://localhost:8081/auth/verify', {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (!verifyResponse.data?.valid) {
        throw new Error('Invalid token received');
      }

      localStorage.setItem('token', token);
      localStorage.setItem('user', JSON.stringify(userData));
      setIsAuthenticated(true);
      setUser(userData);
      
      await loadCartItems(token, userData.email);
      return true;
    } catch (error) {
      console.error('Login error:', error);
      clearAuth();
      throw error;
    }
  };

  const logout = () => {
    clearAuth();
    toast.success('Logged out successfully');
  };

  const clearAuth = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    localStorage.removeItem('cart');
    setIsAuthenticated(false);
    setUser(null);
    setCart([]);
  };

  const updateCart = async (cartItems) => {
    if (!isAuthenticated || !user?.email) {
      throw new Error('User not authenticated');
    }
    
    const token = localStorage.getItem('token');
    if (!token) {
      throw new Error('No authentication token found');
    }

    try {
      setCartLoading(true);
      const response = await axios.post(
        `http://localhost:8081/api/customers/${user.email}/cart/sync`,
        { items: cartItems },
        { 
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      if (response.status === 200) {
        setCart(cartItems);
        localStorage.setItem('cart', JSON.stringify(cartItems));
        return true;
      }
      throw new Error('Failed to update cart');
    } catch (error) {
      console.error('Cart update failed:', error);
      throw error;
    } finally {
      setCartLoading(false);
    }
  };

  return (
    <AuthContext.Provider value={{ 
      isAuthenticated, 
      user,
      loading,
      cart,
      cartLoading,
      login,
      logout,
      updateCart
    }}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  return useContext(AuthContext);
}
