import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';

const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);
  const [cart, setCart] = useState([]);
  const [cartLoading, setCartLoading] = useState(false);

  useEffect(() => {
    let isMounted = true; // To prevent memory leaks
    
    const initializeAuth = async () => {
      const token = localStorage.getItem('token');
      const userData = localStorage.getItem('user');
      
      if (token && userData) {
        try {
          const response = await axios.get('http://localhost:8081/auth/verify', {
            headers: { Authorization: `Bearer ${token}` }
          });
          
          if (isMounted && response.data.valid) {
            setIsAuthenticated(true);
            const parsedUser = JSON.parse(userData);
            setUser(parsedUser);
            await loadCartItems(token, parsedUser.email);
          } else if (isMounted) {
            clearAuth();
          }
        } catch (error) {
          if (isMounted) clearAuth();
        }
      }
      if (isMounted) setLoading(false);
    };
    
    initializeAuth();

    return () => {
      isMounted = false; // Cleanup function
    };
  }, []);

  const loadCartItems = async (token, email) => {
    if (!token || !email) return;
    
    setCartLoading(true);
    try {
      const response = await axios.get(
        `http://localhost:8081/api/customers/${email}/cart`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const cartItems = response.data.items || [];
      setCart(cartItems);
      localStorage.setItem('cart', JSON.stringify(cartItems));
    } catch (error) {
      console.error('Failed to load cart:', error);
      toast.error('Failed to load cart items');
    } finally {
      setCartLoading(false);
    }
  };

  const login = async (token, userData) => {
    try {
      localStorage.setItem('token', token);
      localStorage.setItem('user', JSON.stringify(userData));
      setIsAuthenticated(true);
      setUser(userData);
      await loadCartItems(token, userData.email);
      return true;
    } catch (error) {
      console.error('Login error:', error);
      toast.error('Failed to complete login');
      return false;
    }
  };

  const logout = () => {
    clearAuth();
    toast.success('Logged out successfully');
  };

  const clearAuth = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    localStorage.removeItem('cart');
    setIsAuthenticated(false);
    setUser(null);
    setCart([]);
  };

  const addToCart = async (item) => {
    if (!isAuthenticated || !user) {
      toast.error('Please login to add items to cart');
      return false;
    }
    
    try {
      setCartLoading(true);
      const newCart = [...cart, item];
      await axios.post(
        `http://localhost:8081/api/customers/${user.email}/cart/sync`,
        { items: newCart },
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );
      setCart(newCart);
      localStorage.setItem('cart', JSON.stringify(newCart));
      return true;
    } catch (error) {
      console.error('Failed to add to cart:', error);
      toast.error('Failed to add item to cart');
      return false;
    } finally {
      setCartLoading(false);
    }
  };

  const removeFromCart = async (index) => {
    if (!isAuthenticated || !user || index < 0 || index >= cart.length) {
      return false;
    }
    
    try {
      setCartLoading(true);
      const newCart = [...cart];
      newCart.splice(index, 1);
      await axios.post(
        `http://localhost:8081/api/customers/${user.email}/cart/sync`,
        { items: newCart },
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );
      setCart(newCart);
      localStorage.setItem('cart', JSON.stringify(newCart));
      return true;
    } catch (error) {
      console.error('Failed to remove from cart:', error);
      toast.error('Failed to remove item from cart');
      return false;
    } finally {
      setCartLoading(false);
    }
  };

  return (
    <AuthContext.Provider value={{ 
      isAuthenticated, 
      user,
      loading,
      cart,
      cartLoading,
      login,
      logout,
      addToCart,
      removeFromCart,
      loadCartItems
    }}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}
